import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css']
})
export class CalculatorComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }


  private num1="";
  private numb2="";


  calculateValNumb1(event:Event){

    this.num1 = ((<HTMLInputElement> event.target).value);

    let result = parseInt(this.num1);

    return result;

  }

  getNumb1(){

    let result = this.num1.toString();
    return result;
  }




}


  <br>
  <h3 class="title_calculator text-info">Please, fill the gaps</h3>
  <div>
    <input type="text" placeholder="Type a number" class="input_numb1 form-control">
  </div>

  <div>
    <input type="text" placeholder="Type a number" class="input_numb2 form-control">
  </div>

  <div>
    <button class="button btn btn-info sum_button" (click)="calculateValNumb1($event)">Sum</button>
    <button class="button btn btn-danger subs_button">Subtraction</button>
    <button class="button btn btn-warning mult_button">Multiplication</button>
  </div>

  valor: {{getNumb1()}}
