import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css']
})
export class CalculatorComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }


   num1="";
   num2="";
   sum="";
   subs="";


  calculateValNumb1(event:Event): string{

    return this.num1 = (<HTMLInputElement> event.target).value;

  }


  calculateValNumb2(event:Event):string{


    return this.num2 = (<HTMLInputElement> event.target).value;

  }


  sumResult(){

    const result_values = document.getElementById("result_values");

    let num1Value = parseInt(this.num1);
    let num2Value = parseInt(this.num2);

    let sumValue = num1Value+num2Value;
    result_values!.textContent = sumValue.toString();
    return this.sum = sumValue.toString()

  }

  subsResult(){

    let num1Value = parseInt(this.num1);
    let num2Value = parseInt(this.num2);

    let subsValue = num1Value - num2Value;

    return this.subs = subsValue.toString()


  }






}


  <br>
  <h3 class="title_calculator text-info">Please, fill the gaps</h3>
  <div>
    <input type="text" placeholder="Type a number" class="input_numb1 form-control" (input)="calculateValNumb1($event)">
  </div>

  <div>
    <input type="text" placeholder="Type a number" class="input_numb2 form-control" (input)="calculateValNumb2($event)">
  </div>

  <div>
    <button class="button btn btn-info sum_button" (click)="sumResult()">Sum</button>
    <button class="button btn btn-danger subs_button" (click)="subsResult()">Subtraction</button>
    <button class="button btn btn-warning mult_button">Multiplication</button>
  </div>

  First number value: {{num1}}
  <br>
  Second number value: {{num2}}

  <label for="result_values">Result: </label>
   <p id="result_values"></p>
